import { Box, ButtonGroup, Container, Paper, TableContainer, Table, TableHead, TableRow, TableCell, TableBody, Button } from '@material-ui/core';
import React, { useState, useEffect } from 'react';
import Row from './row/row';
import BodyStyle from './body.style';

function Body(props) {
  const styles = BodyStyle();
  const [rows, setRows] = useState([]);
  const [page, setPage] = useState(0);
  const [total, setTotal] = useState(0);
  const [limit, setLimit] = useState(0);
  const types = ['', 'USUÁRIO', 'EMAIL', 'DATA DE INCLUSÃO', 'DATA DE ALTERAÇÃO', 'REGRAS', 'STATUS', 'AÇÕES']

  async function getUsers(page) {
    try {
      let response = await fetch(
        `http://localhost:8080/user/${page}${props.query}`, {
        method: 'GET',
        headers: {
          Accept: 'application/json',
          'Content-Type': 'application/json'
        }
      });
      let json = await response.json();
      setPage(json.result.page);
      setTotal(json.result.total);
      setLimit(json.result.limit);
      console.log(total)
      console.log(limit)
      if (json.result != null) setRows(Array.from(json.result.users));
      else setRows([]);
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    async function getUsers() {
      try {
        let response = await fetch(
          `http://localhost:8080/user/0`, {
          method: 'GET',
          headers: {
            Accept: 'application/json',
            'Content-Type': 'application/json'
          }
        });
        let json = await response.json();
        setPage(json.result.page);
        setTotal(json.result.total);
        setLimit(json.result.limit);
        if (json.result != null) setRows(Array.from(json.result.users));
        else setRows([]);
      } catch (error) {
        console.error(error);
      }
    };
    getUsers();
  }, []);

  return (
    <Box className={styles.body} minHeight={props.emptySpace}>
      <Container maxWidth={false} className={styles.container}>
        <TableContainer component={Paper} className={styles.tableContainer}>
          <Table className={styles.table} aria-label="user table">
            <TableHead className={styles.head}>
              <TableRow>
                {types.map((type) => (<TableCell key={type} className={styles.headCell}>{type}</TableCell>))}
              </TableRow>
            </TableHead>
            <TableBody>
              {rows.map((row) => (<Row key={row._id} row={row} index={rows.indexOf(row)} />))}
            </TableBody>
          </Table>
        </TableContainer>
        <ButtonGroup className={styles.pagination} aria-label="pagination" >
          <Button
            className={styles.paginationButton}
            style={{ borderRadius: 4 }}
            onClick={() => getUsers(0)}
            disabled={page === 0 ? false : true}
            variant='contained'>Primeiro</Button>
          <Button
            className={styles.paginationButton}
            style={{ borderRadius: 4 }}
            onClick={() => getUsers(page - 1)}
            disabled={page === 0 ? false : true}
            variant='contained'>Anterior</Button>
          <Button
            className={styles.page}
            style={{ borderRadius: 4 }}
            variant='contained'>{page}</Button>
          <Button
            className={styles.paginationButton}
            style={{ borderRadius: 4 }}
            onClick={() => getUsers(page + 1)}
            disabled={total - (page * limit) > 0 ? false : true}
            variant='contained'>Próximo</Button>
          <Button
            className={styles.lastButton}
            style={{ borderRadius: 4 }}
            onClick={() => getUsers(parseInt(total/limit))}
            disabled={total - (page * limit) > 0 ? false : true}
            variant='contained'>último</Button>
        </ButtonGroup >
      </Container>
    </Box>
  );
}

export default Body;
