import { Drawer, Box, TextField, Typography, Button } from '@material-ui/core';
import { Edit as EditIcon, Close as CloseIcon } from '@material-ui/icons';
import React from 'react';
import DrawerStyle from './customDrawer.style';

function customDrawer(props) {
    const styles = DrawerStyle();

    return (
        <Drawer
            anchor="right"
            open={props.open}
            onClose={props.handleClose}>
            <Box className={styles.body}>
                <Box className={styles.head}>
                    <Box className={styles.head}>
                        <EditIcon className={styles.grayIcon} />
                        <Typography className={styles.darkGrayText}>Editar</Typography>
                    </Box>
                    <Button
                        className={styles.headButton}
                        variant='contained'
                        onClick={props.handleClose}>
                        <CloseIcon className={styles.grayButtonIcon} />
                    </Button>
                </Box>
                <form className={styles.form}>
                    <TextField
                        className={styles.formText}
                        id="user"
                        label="UsuÃ¡rio"
                        style={{color:'green'}}
                        defaultValue={props.row.username} />
                    <TextField
                        className={styles.formText}
                        id="email"
                        label="Email"
                        defaultValue={props.row.email} />
                    <TextField
                        className={styles.formText}
                        id="rules"
                        label="Regras"
                        defaultValue={props.row.rules} />
                    <TextField
                        className={styles.formText}
                        id="status"
                        label="Status"
                        defaultValue={props.row.status} />
                    <Button className={styles.formButton} variant='outlined' color='#D83367'>
                        Confirmar
                    </Button>
                </form>
            </Box>
        </Drawer>
    );
}

export default customDrawer;
