import { Drawer, Box, TextField, Typography, Button, Checkbox } from '@material-ui/core';
import { Edit as EditIcon, Close as CloseIcon } from '@material-ui/icons';
import React, { useState, useEffect} from 'react';
import DrawerStyle from './customDrawer.style';

function CustomDrawer(props) {
    const styles = DrawerStyle();
    const [check, setCheck] = useState(false);

    useEffect(() => {
        setCheck(props.row.status);
    }, []);

    async function updateEntry(event) {
        console.log(event);
        try {
            let url = `http://localhost:8080/user/${props.row._id}`;
            let response = await fetch(
                url, {
                method: 'PATCH',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json'
                },
                body: {
                    username: '',
                    email: '',
                    rules: '',
                    status: check
                }
            });
            let json = await response.json();
            if (json.message === 'Usuario atualizado com sucesso') {
                props.handleClose();
                props.getUsersAfterUpdate(0);
            }
        } catch (error) {
            console.error(error);
        }
    };

    return (
        <Drawer
            anchor="right"
            open={props.open}
            onClose={props.handleClose}>
            <Box className={styles.body}>
                <Box className={styles.head}>
                    <Box className={styles.head}>
                        <EditIcon className={styles.grayIcon} />
                        <Typography className={styles.darkGrayText}>{props.type}</Typography>
                    </Box>
                    <Button
                        className={styles.headButton}
                        variant='contained'
                        onClick={props.handleClose}>
                        <CloseIcon className={styles.grayButtonIcon} />
                    </Button>
                </Box>
                <form className={styles.form} onsubmit={updateEntry}>
                    <TextField
                        className={styles.formText}
                        id="user"
                        label="UsuÃ¡rio"
                        type="text"
                        style={{ color: 'green' }}
                        defaultValue={props.row != null ? props.row.username : ''} />
                    <TextField
                        className={styles.formText}
                        id="email"
                        label="Email"
                        type="email"
                        defaultValue={props.row != null ? props.row.email : ''} />
                    <TextField
                        className={styles.formText}
                        id="rules"
                        label="Regras"
                        defaultValue={props.row != null ? props.row.rules : ''} />
                    <Checkbox
                        checked={check}
                        onChange={(event) => { setCheck(event.target.checked) }}
                        inputProps={{ 'aria-label': 'select all desserts' }}
                    />
                    <TextField
                        className={styles.formText}
                        id="status"
                        label="Status"
                        defaultValue={props.row != null ? props.row.status : ''} />
                    <Button
                        className={styles.formButton}
                        variant='outlined'
                        color='#D83367'
                        type="submit">
                        Confirmar
                    </Button>
                </form>
            </Box>
        </Drawer>
    );
}

export default CustomDrawer;
