import { Box, Checkbox, Container, Paper, Button, TableContainer, Table, TableHead, TableRow, TableCell, TableBody } from '@material-ui/core';
import React, { useState, useEffect } from 'react';
import { MoreHoriz as MoreIcon } from '@material-ui/icons';
import BodyStyle from './body.style';

function Body(props) {
  const styles = BodyStyle();
  const [rows, setRows] = useState([]);
  const types = ['', 'USUÁRIO', ]

  async function getUsers() {
    try {
      let response = await fetch(
        'http://localhost:8080/user'
      );
      let json = await response.json();
      if (json.result != null) setRows(Array.from(json.result));
      else setRows([]);

    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    getUsers();
  }, []);

  return (
    <Box className={styles.body} minHeight={props.emptySpace}>
      <Container maxWidth={false}>
        <TableContainer component={Paper} className={styles.container}>
          <Table className={styles.table} aria-label="user table">
            <TableHead className={styles.head}>
              <TableRow>
                <TableCell className={styles.headCell}></TableCell>
                <TableCell className={styles.headCell}>USUÁRIO</TableCell>
                <TableCell className={styles.headCell}>EMAIL</TableCell>
                <TableCell className={styles.headCell}>DATA DE INCLUSÃO</TableCell>
                <TableCell className={styles.headCell}>DATA DE ALTERAÇÃO</TableCell>
                <TableCell className={styles.headCell}>REGRAS</TableCell>
                <TableCell className={styles.headCell}>STATUS</TableCell>
                <TableCell className={styles.headCell} >AÇÕES</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {rows.map((row) => (
                <TableRow key={row._id} className={rows.indexOf(row) % 2 === 0 ? styles.evenRow : styles.oddRow}>
                  <TableCell>
                    <Checkbox
                      inputProps={{ 'aria-label': 'select all desserts' }}
                    />
                  </TableCell>
                  <TableCell className={styles.cell} component="th" scope="row">{row.username}</TableCell>
                  <TableCell className={styles.cell}>{row.email}</TableCell>
                  <TableCell className={styles.cell}>{row.createdAt}</TableCell>
                  <TableCell className={styles.cell}>{row.updatedAt}</TableCell>
                  <TableCell className={styles.cell}>{row.rules}</TableCell>
                  <TableCell className={row.status ? styles.green : styles.red} >{row.status ? "Ativo" : "Inativo"}</TableCell>
                  <TableCell >
                    <Button
                      aria-label='more'
                      className={styles.button}
                      onClick={() => { }}>
                      <MoreIcon className={styles.gray} />
                    </Button>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      </Container>
    </Box>
  );
}

export default Body;
