import { Box, ButtonGroup, Container, Paper, TableContainer, Table, TableHead, TableRow, TableCell, TableBody, Button } from '@material-ui/core';
import React, { useState, useEffect } from 'react';
import Row from './row/row';
import BodyStyle from './body.style';

function Body(props) {
  const styles = BodyStyle();
  const [rows, setRows] = useState([]);
  const types = ['', 'USUÁRIO', 'EMAIL', 'DATA DE INCLUSÃO', 'DATA DE ALTERAÇÃO', 'REGRAS', 'STATUS', 'AÇÕES']
  let page = 0;

  async function getUsers() {
    try {
      let response = await fetch(
        'http://localhost:8080/user', {
        method: 'GET',
        headers: {
          Accept: 'application/json',
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          page: 'yourValue'
        })
      });
      let json = await response.json();
      if (json.result != null) setRows(Array.from(json.result));
      else setRows([]);

    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    getUsers();
  }, []);

  function handleChangePage() {

  };

  return (
    <Box className={styles.body} minHeight={props.emptySpace}>
      <Container maxWidth={false} className={styles.container}>
        <TableContainer component={Paper} className={styles.tableContainer}>
          <Table className={styles.table} aria-label="user table">
            <TableHead className={styles.head}>
              <TableRow>
                {types.map((type) => (<TableCell key={type} className={styles.headCell}>{type}</TableCell>))}
              </TableRow>
            </TableHead>
            <TableBody>
              {rows.map((row) => (<Row key={row._id} row={row} index={rows.indexOf(row)} />))}
            </TableBody>
          </Table>
        </TableContainer>
        <ButtonGroup className={styles.pagination} aria-label="pagination" >
          <Button className={styles.paginationButton} style={{ borderRadius: 4 }} variant='contained'>Primeiro</Button>
          <Button className={styles.paginationButton} style={{ borderRadius: 4 }} variant='contained'>Anterior</Button>
          <Button className={styles.page} style={{ borderRadius: 4 }} variant='contained'>{page}</Button>
          <Button className={styles.paginationButton} style={{ borderRadius: 4 }} variant='contained'>Próximo</Button>
          <Button className={styles.lastButton} style={{ borderRadius: 4 }} variant='contained'>último</Button>
        </ButtonGroup >
      </Container>
    </Box>
  );
}

export default Body;
