import { useState } from 'react';
import { ReactComponent as Logo } from '../images/symbol.svg';
import { AppBar, Toolbar } from '@material-ui/core';
import HeaderStyle from './headerStyle';

function Header(props) {
  const [index, setIndex] = useState(0);
  const [search, setSearch] = useState('');
  const styles = HeaderStyle();

  function changeIndex(pos) {
    setIndex(pos);
    if (pos === 0) props.history.push('security');
    else props.history.push('person');
  }

  function searchUser(event) {
  }

  return (
    <Toolbar className={styles.bar}>
      <Logo className={styles.logo} alt='brand' />
      <hr className='change' />
      <div className='options'>
        <button className={index === 0 ? 'option gradient' : 'option'} onClick={() => changeIndex(0)}>
          <i className='material-icons'>security</i>
        </button>
        <button className={index === 1 ? 'option gradient' : 'option'} onClick={() => changeIndex(1)}>
          <i className='material-icons'>person</i>
        </button>
      </div>
      <form className='form' onSubmit={searchUser} id='header-form'>
        <input
          className='search'
          id='header-search'
          name='search'
          onChange={(e) => setSearch(e.target.value)}
          placeholder={'Pesquisar...'}
          type='text'
          value={search} />
        <input className='material-icons' type='submit' value='search' />
      </form>
      <div className='buttons'>
        <button className='filter' onClick={() => { }}>
          <i className='material-icons'>tune</i>
        </button>
        <button className='add' onClick={() => { }}>
          <i className='material-icons'>person</i>
          INCLUIR USU√ÅRIO
        </button>
      </div>
      <hr className='change' />
      <div className='pages'>
        <button className='button' onClick={() => { }}>
          <i className='material-icons'>home</i>
        </button>
        <button className='button' onClick={() => { }}>
          <i className='material-icons'>settings</i>
        </button>
        <button className='button' onClick={() => { }}>
          <i className='material-icons'>power_settings_new</i>
        </button>
      </div>
    </Toolbar>
  );
}

export default Header;
