import React, { useRef, useEffect, useState } from 'react';
import ReactDOM from 'react-dom';
import Header from './header/header';
import Body from './body/body';
import Footer from './footer/footer';
import ScreenSize from './utils/screen/screen';
import { BrowserRouter, Route } from 'react-router-dom';
import reportWebVitals from './reportWebVitals';
import { createMuiTheme, ThemeProvider } from '@material-ui/core/styles';
import './index.css';

function Index() {
  let theme = createMuiTheme();
  const [emptySpace, setEmptySpace] = useState(0);
  const [getUsersAfterUpdate, setGetUsersAfterUpdate] = useState(null);
  const [query, setQuery] = useState('');
  const size = ScreenSize();
  const headerRef = useRef(null);
  const footerRef = useRef(null);

  useEffect(() => {
    function setSpace() {
      if (headerRef.current && footerRef.current)
        setEmptySpace(size[1] - headerRef.current.clientHeight - footerRef.current.clientHeight);
      else
        setEmptySpace(size[1]);
    }
    setSpace();
  });

  function setFunc(func) {
    setGetUsersAfterUpdate(() => func);
  }

  function setSearch(search) {
    setQuery(search);
  }

  return (
    <ThemeProvider theme={theme}>
      <Header key='header' ref={headerRef} getUsersAfterUpdate={getUsersAfterUpdate} setQuery={setSearch}/>
      <Body key='body' emptySpace={emptySpace} func={setFunc} query={query}/>
      <Footer key='footer' ref={footerRef} />
    </ThemeProvider>
  );
}

ReactDOM.render(
  <BrowserRouter>
    <Route path='' render={() => <Index />} />
  </BrowserRouter>,
  document.getElementById('root')
);

reportWebVitals();
