import { Button, Box, Menu, MenuItem, Drawer } from '@material-ui/core';
import React, { useState } from 'react';
import { MoreHoriz as MoreIcon, Edit as EditIcon, Delete as DeleteIcon } from '@material-ui/icons';
import MenuStyle from './customMenu.style';
import PopupState, { bindTrigger, bindMenu } from 'material-ui-popup-state';
import CustomModal from '../modal/customModal';
import CustomDrawer from '../../drawer/customDrawer';

function CustomMenu(props) {
    const styles = MenuStyle();
    const [open, setOpen] = useState(false);
    const [anchor, setAnchor] = useState(false);
    const ref = useRef(null);
    function handleClose() {
        setOpen(false);
    };

    return (
        <PopupState variant="popover" popupId="menu">
            {(popupState) => (
                <Box>
                    <Button
                        aria-label='more'
                        {...bindTrigger(popupState)}>
                        <MoreIcon className={styles.gray} />
                    </Button>
                    <Menu
                        transformOrigin={{
                            vertical: 'center',
                            horizontal: 'center',
                        }}
                        className={styles.menu}
                        {...bindMenu(popupState)}>
                        <MenuItem className={styles.menuItem} onClick={() => { setOpen(true) }}>
                            <DeleteIcon className={styles.gray} />
                        </MenuItem>
                        <MenuItem ref={ref} className={styles.menuItem} onClick={() => { }}>
                            <EditIcon className={styles.gray} />
                        </MenuItem>
                        <CustomModal open={open} handleClose={handleClose} ids={props.id} getUsersAfterDelete={props.getUsersAfterDelete} />
                        <Drawer anchor={ref} open={state[anchor]} onClose={toggleDrawer(anchor, false)}>
                            <CustomDrawer />
                        </Drawer>
                    </Menu>
                </Box>
            )
            }
        </PopupState >
    );
}

export default CustomMenu;
