import './header.css';
import { useState } from 'react';
import { ReactComponent as Logo } from '../images/symbol.svg';

function Header(props) {
  const [index, setIndex] = useState(0);
  const [search, setSearch] = useState('');

  function changeIndex(pos) {
    setIndex(pos);
    if (pos === 0) props.history.push('security');
    else props.history.push('person');
  }

  return (
    <header className='Header'>
      <Logo className='logo' alt='brand' />
      <hr className='change' />
      <div className='options'>
        <button className={index === 0 ? 'option gradient' : 'option'} onClick={() => changeIndex(0)}>
          <i className='material-icons'>security</i>
        </button>
        <button className={index === 1 ? 'option gradient' : 'option'} onClick={() => changeIndex(1)}>
          <i className='material-icons'>person</i>
        </button>
        <input
          className='search'
          value={search}
          placeholder={'Pesquisar...'}
          onChange={(e) => setSearch(e.target.value)}
        />
      </div>
    </header>
  );
}

export default Header;
