import { Box, Container, Paper, TableContainer, Table, TableHead, TableRow, TableCell, TableBody, TablePagination } from '@material-ui/core';
import React, { useState, useEffect } from 'react';
import Row from './row/row';
import BodyStyle from './body.style';

function Body(props) {
  const styles = BodyStyle();
  const [rows, setRows] = useState([]);
  const types = ['', 'USUÁRIO', 'EMAIL', 'DATA DE INCLUSÃO', 'DATA DE ALTERAÇÃO', 'REGRAS', 'STATUS', 'AÇÕES']
  const rowsPerPage = 10;
  let page = 0;

  async function getUsers() {
    try {
      let response = await fetch(
        'http://localhost:8080/user'
      );
      let json = await response.json();
      if (json.result != null) setRows(Array.from(json.result));
      else setRows([]);

    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    getUsers();
  }, []);

  function handleChangePage() {

  };

  return (
    <Box className={styles.body} minHeight={props.emptySpace}>
      <Container maxWidth={false}>
        <TableContainer component={Paper} className={styles.container}>
          <Table className={styles.table} aria-label="user table">
            <TableHead className={styles.head}>
              <TableRow>
                {types.map((type) => (<TableCell key={type} className={styles.headCell}>{type}</TableCell>))}
              </TableRow>
            </TableHead>
            <TableBody>
              {rows.map((row) => (<Row key={row._id} row={row} index={rows.indexOf(row)}/>))}
            </TableBody>
          </Table>
        </TableContainer>
        <TablePagination
          component="container"
          count={rows.length}
          rowsPerPage={rowsPerPage}
          page={page}
          onChangePage={handleChangePage}
        />
      </Container>
    </Box>
  );
}

export default Body;
