import { Box, Checkbox, Container, Paper, Button, TableContainer, Table, TableHead, TableRow, TableCell, TableBody } from '@material-ui/core';
import React, { useState, useEffect } from 'react';
import { MoreHoriz as MoreIcon } from '@material-ui/icons';
import BodyStyle from './body.style';

function Body(props) {
  const styles = BodyStyle();
  const [rows, setRows] = useState([]);

  async function getUsers() {
    try {
      let response = await fetch(
        'http://localhost:8080/user'
      );
      let json = await response.json();
      if (json.result != null) setRows(Array.from(json.result));
      else setRows([]);

    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    getUsers();
  }, []);

  return (
    <Box className={styles.body} minHeight={props.emptySpace}>
      <Container maxWidth={false}>
        <TableContainer component={Paper} className={styles.container}>
          <Table className={styles.table} aria-label="user table">
            <TableHead className={styles.head}>
              <TableRow>
                <TableCell ></TableCell>
                <TableCell >USUÁRIO</TableCell>
                <TableCell >EMAIL</TableCell>
                <TableCell >DATA DE INCLUSÃO</TableCell>
                <TableCell >DATA DE ALTERAÇÃO</TableCell>
                <TableCell >REGRAS</TableCell>
                <TableCell >STATUS</TableCell>
                <TableCell >AÇÕES</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {rows.map((row) => (
                <TableRow key={row._id} className={rows.indexOf(row) % 2 === 0 ? styles.evenRow : styles.oddRow}>
                  <Checkbox
                    inputProps={{ 'aria-label': 'select all desserts' }}
                  />
                  <TableCell component="th" scope="row">{row.username}</TableCell>
                  <TableCell >{row.email}</TableCell>
                  <TableCell >{row.createdAt}</TableCell>
                  <TableCell >{row.updatedAt}</TableCell>
                  <TableCell >{row.rules}</TableCell>
                  <TableCell className={row.status ? styles.green : styles.red} >{row.status? "Ativo" : "Inativo"}</TableCell>
                  <Button
                    aria-label='more'
                    onClick={() => { }}>
                    <MoreIcon className={styles.gray} />
                  </Button>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      </Container>
    </Box>
  );
}

export default Body;
